mapper.insert:

		
		INSERT INTO LETTNQAINFO
			   (QA_ID
			  , QESTN_SJ
			  , QESTN_CN
			  , WRITNG_PASSWORD
			  , AREA_NO
			  , MIDDLE_TELNO
			  , END_TELNO  
			  ,	EMAIL_ADRES
			  , EMAIL_ANSWER_AT
			  , WRTER_NM
			  , WRITNG_DE
			  , RDCNT
			  , QNA_PROCESS_STTUS_CODE  					     
			  , FRST_REGIST_PNTTM 
			  , FRST_REGISTER_ID )
		VALUES ( #{qaId}
			  , #{qestnSj}			
			  , #{qestnCn}
			  , #{writngPassword}
			  , #{areaNo}			
			  , #{middleTelno}
			  , #{endTelno}
			  , #{emailAdres}			
			  , #{emailAnswerAt}
			  , #{wrterNm}
			  , TO_CHAR(sysdate,'YYYYMMDD')			
			  , 0
			  , 1				  
			  , sysdate
			  , #{frstRegisterId})

mapper.update:
				
			UPDATE	LETTNQAINFO	SET
				    RDCNT= RDCNT + 1				  			
 			WHERE  QA_ID=#{qaId}

mapper.select:

	SELECT 	COUNT(*) totcnt
	FROM 	LETTNQAINFO
	WHERE	QA_ID=#{qaId}
	AND		WRITNG_PASSWORD=#{writngPassword}

mapper.update:

	UPDATE	LETTNQAINFO	SET 
		  	QESTN_SJ=#{qestnSj}
		  , QESTN_CN=#{qestnCn}
		  , WRITNG_PASSWORD=#{writngPassword}				  
		  , AREA_NO=#{areaNo}
		  , MIDDLE_TELNO=#{middleTelno}
		  , END_TELNO=#{endTelno}
		  ,	EMAIL_ADRES=#{emailAdres}
		  , EMAIL_ANSWER_AT=NVL(#{emailAnswerAt},'N')
		  , WRTER_NM=#{wrterNm}
		  , WRITNG_DE=TO_CHAR(sysdate,'YYYYMMDD')
		  , FRST_REGIST_PNTTM=sysdate
		  , FRST_REGISTER_ID=#{frstRegisterId}
	WHERE  QA_ID=#{qaId}


	UPDATE	LETTNQAINFO	SET 
		  	QNA_PROCESS_STTUS_CODE=#{qnaProcessSttusCode}
		  , ANSWER_CN=#{answerCn}
		  , ANSWER_DE=TO_CHAR(sysdate,'YYYYMMDD')
		  , LAST_UPDT_PNTTM=sysdate
		  , LAST_UPDUSR_ID=#{lastUpdusrId}
	WHERE  QA_ID=#{qaId}
