mapper.update:
UPDATE LETTNQUSTNRTMPLAT SET
QUSTNR_TMPLAT_IMAGE_INFO = #{qestnrTmplatImagepathnm,jdbcType=BLOB},
QUSTNR_TMPLAT_TY = #{qestnrTmplatTy}, QUSTNR_TMPLAT_DC = #{qestnrTmplatCn}, QUSTNR_TMPLAT_PATH_NM = #{qestnrTmplatCours}, LAST_UPDT_PNTTM = sysdate, LAST_UPDUSR_ID = #{lastUpdusrId} WHERE QUSTNR_TMPLAT_ID = #{qestnrTmplatId}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.QUSTNR_TMPLAT_ID AS "qestnrTmplatId", A.QUSTNR_TMPLAT_TY AS "qestnrTmplatTy", A.FRST_REGIST_PNTTM AS "frstRegisterPnttm", (SELECT USER_NM FROM COMVNUSERMASTER WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, A.FRST_REGISTER_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.QUSTNR_TMPLAT_IMAGE_INFO AS "qestnrTmplatImagepathnm", A.QUSTNR_TMPLAT_DC AS "qestnrTmplatCn", A.QUSTNR_TMPLAT_PATH_NM AS "qestnrTmplatCours" FROM LETTNQUSTNRTMPLAT A WHERE 1=1
AND QUSTNR_TMPLAT_DC LIKE '%' || #{searchKeyword} || '%'
AND QUSTNR_TMPLAT_TY LIKE '%' || #{searchKeyword} || '%'
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
